import React, { useState, useEffect } from "react";
import _ from "lodash";
import {
  Layout,
  Row,
  Col,
  Form,
  Input,
  InputNumber,
  Divider,
  Avatar,
  Button,
  Typography,
  Select,
} from "antd";
import { getUser } from "../../../../services/userServices";
import { name } from "../../../../config.json";
import { openNotification } from "../../../custom";

const Profile = () => {
  const [user, setUser] = useState([]);
  const [currentUser] = useState(localStorage.getItem(name));
  const [first_name, setName] = useState(user.first_name);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        await getUser()
          .then((res) => res.data)
          .then((data) => setUser(_.find(data, ["last_name", currentUser])));
      } catch (error) {
        openNotification("error", "Can't get Orders", "Check Connectin");
      }
    };
    fetchUser();
  }, [currentUser]);

  const handleUpdate = (info) => {
    info.preventDefault();
    console.log(first_name);
  };

  return (
    <Layout.Content style={{ margin: "2em 16px" }}>
      <div
        style={{ background: "#fff", padding: 24, minHeight: 280 }}
        className="border border-dark rounded"
      >
        <div className="container">
          <Row>
            <Col>
              <div>
                <Avatar
                  style={{ backgroundPosition: "center" }}
                  src="https://images.unsplash.com/photo-1485875437342-9b39470b3d95?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=800&q=60"
                  size={150}
                  alt="User image"
                />
              </div>
            </Col>
            <Col offset={4}>
              <div>
                <h2>
                  {`${user.first_name}
                ${user.last_name}`}
                </h2>
                <p className="font-weight-light">
                  {user.work_position}
                  <span style={{ margin: "0 0.4em" }}>|</span>
                  {user.email}
                </p>
              </div>
            </Col>
          </Row>
        </div>
      </div>
      <div
        style={{ background: "#fff", marginTop: 20, minHeight: 280 }}
        className="border border-dark rounded"
      >
        {/* <Form onFinish={handleUpdate}>
          <Form.Item name="first_name">
            <Input value={user.first_name} />
          </Form.Item>
          <Form.Item>
            <Input value={user.last_name} />
          </Form.Item>
          <Form.Item>
            <Select className="w-100" placeholder="Choose your gender">
              <Select.Option value="Male">Male</Select.Option>
              <Select.Option value="Female">Female</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item>
            <Button htmlType="submit">Edit</Button>
          </Form.Item>
        </Form> */}
        Frirst Name:
        <input
          type="text"
          value={first_name}
          onChange={(e) => setName(e.target.value)}
        />
        <Button onClick={handleUpdate}></Button>
      </div>
    </Layout.Content>
  );
};

export default Profile;
