// React
import React, { useState, useEffect } from "react";

// Third Part
import { Table, Layout, Input, Divider } from "antd";
import _ from "lodash";

// custom
import { openNotification } from "../../../custom";

// services
import { getReservation } from "../../../../services/reservationServices";

const SearchReservation = () => {
  // state
  const [reserv, searchReserv] = useState([]);

  // get Table
  const fetchReservation = async () => {
    await getReservation()
      .then(res => res.data)
      .then(data => searchReserv(data));
  };

  // hook
  useEffect(() => {
    fetchReservation();
  }, []);

  // searchMethod
  const onSearchItem = value => {
    const name = value.currentTarget.value;
    const customer = _.find(reserv, ["customer_name", name]);
    const newData = { ...customer };
    console.log(customer);
  };

  return (
    <Layout.Content
      style={{ margin: "2em 16px" }}
      className="border border-danger"
    >
      <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
        <Input
          onChange={value => onSearchItem(value)}
          placeholder="Enter Customer Name"
          className="w-25"
        />
        <div className="w-25">
          <Divider />
        </div>
        <Table>
          <Table.Column title="Customers" />
        </Table>
      </div>
    </Layout.Content>
  );
};

export default SearchReservation;
