import React from "react";
import DishModalForm from "./dishModalForm";
import { Modal, Row, Col, Form, Input, InputNumber, Typography } from "antd";

const DishModal = ({ visible, data, onhandleUpdate, onCancel }) => {
  const [form] = Form.useForm();
  return (
    <Modal
      closable={false}
      visible={visible}
      okText="Edit"
      cancelText="Cancel"
      onCancel={onCancel}
      onOk={() => {
        form
          .validateFields()
          .then(values => {
            form.resetFields();
            onhandleUpdate(values);
          })
          .catch(info => {
            console.log("Validate Failed:", info);
          });
      }}
    >
      <Form
        hideRequiredMark
        form={form}
        layout="vertical"
        name="form_in_modal"
        initialValues={{
          id: data.id,
          name: data.name,
          price: data.price,
          type: data.type,
          date: data.date
        }}
      >
        <Form.Item name="id">
          <Typography level={4}>Edit Element</Typography>
        </Form.Item>
        <Row>
          <Col span={15}>
            <Form.Item
              name={firstData}
              label={firstData}
              rules={[
                {
                  required: true,
                  message: `Please input the ${firstData}!`
                }
              ]}
            >
              <Input />
            </Form.Item>
          </Col>
          <Col span={5} offset={2}>
            <Form.Item
              name={secondData}
              label={secondData}
              rules={[
                {
                  required: true,
                  message: `Please input the ${secondData}!`
                }
              ]}
            >
              <InputNumber
                min={0}
                step={0.1}
                formatter={secondData => `¥ ${secondData}`}
              />
            </Form.Item>
          </Col>
        </Row>
        <Form.Item
          name={thirdData}
          label={thirdData}
          rules={[
            {
              required: true,
              message: `Please input the ${thirdData}!`
            }
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={fourthData}
          label={fourthData}
          rules={[
            {
              required: true,
              message: `Please input the ${fourthData}!`
            }
          ]}
        >
          <Input />
        </Form.Item>
      </Form>
    </Modal>
  );
};

export default DishModal;
