// React
import React, { Fragment, useEffect, useState } from "react";

// Third Part
import { Layout } from "antd";

// Custom
import { HeaderTitle, CustomTable, CustomModal } from "../common";
// Services
import {
  getDishes,
  addDishes,
  deleteDishes
} from "../../services/dishServices";

const { Content } = Layout;

const CustomContent = () => {
  // initializing states
  const [data, setData] = useState([]);
  const [visible, setVisible] = useState();
  const [editData, setEditData] = useState({});

  //   hook cycles
  const fetchDishData = async () => {
    await getDishes()
      .then(res => res.data)
      .then(res => setData(res))
      .catch(err => console.log("Error in dish"));
  };

  //   Modal handle
  const onCancel = item => {
    setVisible(false);
  };

  const onhandleAdd = item => {
    console.log(item);
    // setVisible(false);
  };

  //   handle actions in table item
  const deleteItem = async item => {
    const orginaldata = data;
    try {
      await deleteDishes(item);
    } catch (error) {
      setData(orginaldata);
    }
  };

  const editItem = async item => {
    const newData = { ...item };
    setEditData(newData);
    console.log(editData);
    setVisible(true);
  };

  useEffect(() => {
    fetchDishData();
  }, []);
  return (
    <Fragment>
      <Content style={{ margin: "2em 16px" }}>
        <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
          <HeaderTitle title={"Dish Menu"} />
          <CustomTable
            data={data}
            colOne={"name"}
            colTwo={"price"}
            colThird={"type"}
            colFourth={"date"}
            colFive={"Actions"}
            deleteItem={deleteItem}
            editItem={editItem}
          />
          <CustomModal
            visible={visible}
            onhandleAdd={onhandleAdd}
            onCancel={onCancel}
            itemOne={editData.name}
            itemSecond={editData.price}
            itemThird={editData.type}
            itemFourth={editData.date}
          />
        </div>
      </Content>
    </Fragment>
  );
};

export default CustomContent;
