// react
import React, { Component, Fragment } from "react";

// third part
import { Layout, Menu, Typography, Button } from "antd";
import { HashRouter as Router, Route, Link } from "react-router-dom";

// custom
import "../../assets/css/AdminForm.css";
import Breakfast from "../../assets/icons/Breakfast";
import PersonalIcon from "../../assets/icons/Personal";
import TableInfo from "../../assets/icons/Table_Info";
import Dish from "./components/Dish";
import Personal from "./components/Personal";
import Table from "./components/TableRes";

const { Header, Content, Footer, Sider } = Layout;
const { Text } = Typography;

export default class AdminBoard extends Component {
  render() {
    return (
      <Fragment>
        <Router>
          <Layout style={{ minHeight: "100vh" }}>
            <Sider>
              <div className="logo">
                <Text className="text" type="warning">
                  PLOV
                </Text>
              </div>

              <Menu theme="light" mode="inline">
                <Menu.Item key="1">
                  <Breakfast />
                  <span className="menu-title">Dish information</span>
                  <Link to="/dish" />
                </Menu.Item>
                <Menu.Item key="2">
                  <PersonalIcon />
                  <span className="menu-title">Personal Information</span>
                  <Link to="/personal" />
                </Menu.Item>

                <Menu.Item key="3">
                  <TableInfo />
                  <span className="menu-title">Table Information</span>
                  <Link to="/table" />
                </Menu.Item>
                <Menu.Item>
                  <Button
                    type="link"
                    size="small"
                    style={{ width: "150px" }}
                    onClick={() => this.props.history.push("/")}
                  >
                    Logout
                  </Button>
                </Menu.Item>
              </Menu>
            </Sider>
            <Layout>
              <Header style={{ background: "#000000", padding: 0 }} />
              <Content style={{ margin: "2em 16px" }}>
                <Route exact path="/dish" component={Dish} />
                <Route exact path="/personal" component={Personal} />
                <Route exact path="/table" component={Table} />
              </Content>
              <Footer style={{ textAlign: "center" }}>
                PLOV ©2020 Created by Arian
              </Footer>
            </Layout>
          </Layout>
        </Router>
      </Fragment>
    );
  }
}
