import React, { Component, Fragment } from "react";
import { Layout, Table, Popconfirm } from "antd";

const { Content } = Layout;
export default class Dish extends Component {
  constructor(props) {
    super(props);
    this.columns = [
      {
        title: "Name",
        dataIndex: "name"
      },
      {
        title: "Price",
        className: "column-price",
        dataIndex: "price"
      },
      {
        title: "Type",
        dataIndex: "type"
      },
      {
        title: "Action",
        dataIndex: "operation",
        render: (text, record) =>
          this.state.dataSource.length >= 1 ? (
            <Popconfirm
              title="Sure to delete ?"
              placement="top"
              onConfirm={() => this.handleDelete(record.key)}
              style={{ width: "10px" }}
            >
              <a>Delete</a>
            </Popconfirm>
          ) : null
      }
    ];

    // state

    this.state = {
      dataSource: [
        {
          key: "1",
          name: "John Brown",
          price: "￥300,000.00",
          type: "New York No. 1 Lake Park"
        },
        {
          key: "2",
          name: "Jim Green",
          price: "￥1,256,000.00",
          type: "London No. 1 Lake Park"
        },
        {
          key: "3",
          name: "Joe Black",
          price: "￥120,000.00",
          type: "Sidney No. 1 Lake Park"
        },
        {
          key: "4",
          name: "Joe Black",
          price: "￥120,000.00",
          type: "Sidney No. 1 Lake Park"
        },
        {
          key: "5",
          name: "Joe Black",
          price: "￥120,000.00",
          type: "Sidney No. 1 Lake Park"
        },
        {
          key: "6",
          name: "Joe Black",
          price: "￥120,000.00",
          type: "Sidney No. 1 Lake Park"
        }
      ],
      count: 6
    };
  }

  handleDelete = key => {
    const dataSource = [...this.state.dataSource];
    this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
  };

  render() {
    const { dataSource } = this.state;
    return (
      <Fragment>
        <Content style={{ margin: "2em 16px" }}>
          <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
            <Table
              columns={this.columns}
              dataSource={dataSource}
              bordered
              title={() => "Dish Menu"}
              footer={() => "Footer"}
              scroll={{ y: 240 }}
              pagination={{ pageSize: 50 }}
            />
          </div>
        </Content>
      </Fragment>
    );
  }
}
