import React, { Component, Fragment } from "react";
import { Layout, Table, Popconfirm, Button } from "antd";
import { Resizable } from "react-resizable";

const ResizeableTitle = props => {
  const { onResize, width, ...restProps } = props;

  if (!width) {
    return <th {...restProps} />;
  }

  return (
    <Resizable
      width={width}
      height={0}
      onResize={onResize}
      draggableOpts={{ enableUserSelectHack: false }}
    >
      <th {...restProps} />
    </Resizable>
  );
};

const { Content } = Layout;
export default class Dish extends Component {
  constructor(props) {
    super(props);

    this.state = {
      columns: [
        {
          title: "Name",
          dataIndex: "name"
        },
        {
          title: "Price",
          dataIndex: "price"
        },
        {
          title: "Type",
          dataIndex: "type"
        },
        {
          title: "Action",
          dataIndex: "operation",
          render: (text, record) =>
            this.state.dataSource.length >= 1 ? (
              <Button
                size="small"
                style={{ width: "50px" }}
                onClick={() => this.handleDelete(record.key)}
              >
                Delete
              </Button>
            ) : null
        }
      ],

      dataSource: [
        {
          key: "1",
          name: "Fried Rice",
          price: "￥8",
          type: "Rice&Noodes"
        },
        {
          key: "2",
          name: "Liten kebab",
          price: "￥12",
          type: "Kebab"
        },
        {
          key: "3",
          name: "Margarita",
          price: "￥35",
          type: "Pissa"
        },
        {
          key: "4",
          name: "French toast",
          price: "￥140",
          type: "Snack"
        }
      ],
      count: 4
    };
  }

  handleDelete = key => {
    const dataSource = [...this.state.dataSource];
    this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
  };

  components = {
    header: {
      cell: ResizeableTitle
    }
  };
  handleAdd = () => {
    const { count, dataSource } = this.state;
    const newData = {
      key: count,
      name: `Murskoy kapriz `,
      price: "¥25",
      type: `salad `
    };
    this.setState({
      dataSource: [...dataSource, newData],
      count: count + 1
    });
  };

  handleResize = index => (e, { size }) => {
    this.setState(({ columns }) => {
      const nextColumns = [...columns];
      nextColumns[index] = {
        ...nextColumns[index],
        width: size.width
      };
      return { columns: nextColumns };
    });
  };

  render() {
    const columns = this.state.columns.map((col, index) => ({
      ...col,
      onHeaderCell: column => ({
        width: column.width,
        onResize: this.handleResize(index)
      })
    }));
    const { dataSource } = this.state;
    return (
      <Fragment>
        <Content style={{ margin: "2em 16px" }}>
          <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
            <Button
              onClick={this.handleAdd}
              type="primary"
              style={{ marginBottom: 16, width: "100px" }}
            >
              Add a row
            </Button>
            <Table
              components={this.components}
              columns={columns}
              dataSource={dataSource}
              bordered
              title={() => "Dish Menu"}
              footer={() => "Footer"}
              scroll={{ y: 240 }}
              pagination={{ pageSize: 50 }}
            />
          </div>
        </Content>
      </Fragment>
    );
  }
}
