// React
import React from "react";

// Third Part
import { Drawer, Form, Row, Col, Button, InputNumber, DatePicker } from "antd";

// custom layout size
const layout = {
  labelCol: { span: 7 },
  wrapperCol: { span: 24 }
};
const TableDrawer = ({ visible, onClose, onHandleAdd }) => {
  return (
    <Drawer
      title="Add Table Items"
      width={400}
      closable={false}
      visible={visible}
      onClose={onClose}
    >
      <Form {...layout} onFinish={onHandleAdd} name="basic" hideRequiredMark>
        <Form.Item
          label="Table Number"
          name="table_number"
          rules={[{ required: true, message: "Enter table Number" }]}
        >
          <InputNumber min={0} className="w-100" />
        </Form.Item>
        <Form.Item
          label="Table Seats"
          rules={[{ required: true, message: "Enter table Seats" }]}
          name="seats"
        >
          <InputNumber min={0} className="w-100" />
        </Form.Item>
        <Form.Item
          label="Entered Date"
          rules={[{ required: true, message: "Enter the date" }]}
          name="date"
        >
          <DatePicker picker="date" className="w-100" />
        </Form.Item>
        <Form.Item>
          <Row>
            <Col>
              <Button type="primary" htmlType="submit">
                Add
              </Button>
            </Col>
            <Col offset={2}>
              <Button type="danger" onClick={onClose}>
                Cancel
              </Button>
            </Col>
          </Row>
        </Form.Item>
      </Form>
    </Drawer>
  );
};

export default TableDrawer;
