import React, { Component, Fragment } from "react";
import _ from "lodash";
import {
  Layout,
  Row,
  Col,
  Form,
  Input,
  InputNumber,
  Divider,
  Button,
  Typography,
  Select,
} from "antd";
import { getUser } from "../../../../services/userServices";
import { name } from "../../../../config.json";
import { openNotification } from "../../../custom";
import UserInfo from "./userInfo";

class Profile extends Component {
  state = {
    user: [],
    id: "",
    first_name: "",
    last_name: "",
    age: "",
    gender: "",
  };
  async componentDidMount() {
    await getUser()
      .then((res) => res.data)
      .then((data) =>
        this.setState({
          user: _.find(data, ["last_name", localStorage.getItem(name)]),
        })
      );
    this.setState({
      id: this.state.user.id,
      first_name: this.state.user.first_name,
      last_name: this.state.user.last_name,
      gender: this.state.user.gender,
      age: this.state.user.age,
    });
  }
  handleUpdate = () => {
    const { id, first_name, last_name, age, gender } = this.state;
    const newData = {
      id: id,
      first_name: first_name,
      last_name: last_name,
      age: age,
      gender: gender,
    };
    console.log(newData);
  };
  handleChange = (event) => {
    const value = event.target.value;
    this.setState({
      ...this.state,
      [event.target.name]: value,
    });
  };
  render() {
    const { user, first_name, last_name, gender, age } = this.state;
    return (
      <Fragment>
        <UserInfo data={user} />
        <Input
          value={first_name}
          name="first_name"
          onChange={this.handleChange}
        />
        <select value={gender} name="gender" onChange={this.handleChange}>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
        </select>
        <Input
          value={last_name}
          name="last_name"
          onChange={this.handleChange}
        />
        <Input value={age} name="age" onChange={this.handleChange} />
        <Button onClick={this.handleUpdate}>Edit</Button>
      </Fragment>
    );
  }
}

export default Profile;
