import React, { Component, Fragment } from "react";
import {
  Layout,
  Table,
  Button,
  InputNumber,
  Input,
  Form,
  Row,
  Col
} from "antd";
import { Resizable } from "react-resizable";
import "../../../assets/css/AdminForm.css";
import WrappedDishForm from "../../common/dishDrawer";
import Title from "../../common/title";
import {
  getDishes,
  addDishes,
  deleteDishes
} from "../../../services/dishServices";

// for resizing
const ResizeableTitle = props => {
  const { onResize, width, ...restProps } = props;

  if (!width) {
    return <th {...restProps} />;
  }
  return (
    <Resizable
      width={width}
      height={0}
      onResize={onResize}
      draggableOpts={{ enableUserSelectHack: false }}
    >
      <th {...restProps} />
    </Resizable>
  );
};

const { Content } = Layout;

// Here Started Dish Component
class Dish extends Component {
  async populateDishTable() {
    const { data: dataSource } = await getDishes();
    this.setState({ dataSource });
  }

  async componentDidMount() {
    await this.populateDishTable();
  }

  state = {
    columns: [
      {
        title: "Name",
        dataIndex: "name",
        editable: true
      },
      {
        title: "Price",
        dataIndex: "price",
        editable: true
      },
      {
        title: "Type",
        dataIndex: "type",
        editable: true
      },
      {
        title: "Date",
        dataIndex: "date",
        editable: true
      },
      {
        title: "Action",
        dataIndex: "operation",
        render: (text, record) => {
          <Fragment>
            <Button
              type="danger"
              size="small"
              style={{ width: "60px", margin: " 0 1em" }}
              onClick={() => this.handleDelete(record.id)}
            >
              Delete
            </Button>
            <Button
              type="primary"
              size="small"
              style={{ width: "60px" }}
              // onClick={}
            >
              Edit
            </Button>
          </Fragment>;
        }
      }
    ],

    visible: false,
    dataSource: [],
    title: "Dish Menu"
  };

  cancel = () => {
    this.setState({ editingKey: "" });
  };
  edit(id) {
    this.setState({ editingKey: id });
  }

  handleDelete = async id => {
    const orginaldata = this.state.dataSource;
    try {
      await deleteDishes(id);
    } catch (err) {
      this.setState({ dataSource: orginaldata });
    }
  };

  components = {
    header: {
      cell: ResizeableTitle
    },
    body: {
      cell: EditableCell
    }
  };

  handleAdd = async e => {
    e.preventDefault();
    await this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        addDishes(values);
        this.setState({ visible: false });
      }
    });
  };

  showDrawer = () => {
    this.setState({
      visible: true
    });
  };
  onClose = () => {
    this.setState({
      visible: false
    });
  };
  handleResize = index => (e, { size }) => {
    this.setState(({ columns }) => {
      const nextColumns = [...columns];
      nextColumns[index] = {
        ...nextColumns[index],
        width: size.width
      };
      return { columns: nextColumns };
    });
  };

  render() {
    const columns = this.state.columns.map((col, index) => {
      if (!col.editable) {
        return col;
      }
      return {
        ...col,
        onHeaderCell: column => ({
          width: column.width,
          onResize: this.handleResize(index)
        }),
        onCell: record => ({
          record,
          inputType: col.dataIndex === "age" ? "number" : "text",
          dataIndex: col.dataIndex,
          title: col.title,
          editing: this.isEditing(record.id)
        })
      };
    });
    const { dataSource, visible, title } = this.state;
    return (
      <Fragment>
        <Content style={{ margin: "2em 16px" }}>
          <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
            <Title title={title} />
            <div style={{ textAlign: "right" }}>
              <Button
                type="primary"
                onClick={this.showDrawer}
                style={{ margin: "2em" }}
              >
                Add
              </Button>
            </div>
            <WrappedDishForm
              onClose={this.onClose}
              visible={visible}
              form={this.props.form}
              handleSubmit={this.handleAdd}
            />
            <EditableContext.Provider value={this.props.form}>
              <Table
                components={this.components}
                columns={columns}
                dataSource={dataSource}
                bordered
                rowClassName="editable-row"
                title={() => "Dish Menu"}
                footer={() => "Footer"}
                pagination={false}
                scroll={{ y: 500 }}
              />
            </EditableContext.Provider>
          </div>
        </Content>
      </Fragment>
    );
  }
}

const WrappedHorizontalLoginForm = Form.create()(Dish);
export default WrappedHorizontalLoginForm;
