import React, { Fragment } from "react";
import { Form, Input, Layout, Typography, Row, Col, Button } from "antd";
import MenuFooter from "../custom/footer";

import { remember } from "../../services/userServices";
import { openNotification } from "../custom";

const RememberPassword = () => {
  const onhandleSend = async item => {
    try {
      await remember(item);
    } catch (error) {
      openNotification("error", "Can't send email", "Check connection");
    }
  };
  return (
    <Fragment>
      <Layout>
        <Layout.Content style={{ margin: "5em 16px" }}>
          <div
            style={{
              background: "#fff",
              padding: 44,
              minHeight: 380
            }}
            className="border border-info"
          >
            <Form hideRequiredMark onFinish={onhandleSend}>
              <Row justify="center">
                <Typography.Title className="mb-5">
                  Remember Password
                </Typography.Title>
              </Row>
              <Row justify="center">
                <Col span={5}>
                  <Form.Item
                    name="email"
                    label="Email"
                    rules={[
                      {
                        required: true,
                        type: "email",
                        message: "Enter correct email address"
                      }
                    ]}
                  >
                    <Input />
                  </Form.Item>
                </Col>
                <Col offset={1}>
                  <Form.Item>
                    <Button type="primary" htmlType="submit">
                      Send
                    </Button>
                  </Form.Item>
                </Col>
              </Row>
            </Form>
          </div>
        </Layout.Content>
      </Layout>
      <MenuFooter />
    </Fragment>
  );
};

export default RememberPassword;
