// React
import React, { Fragment, useEffect, useState } from "react";

// Third Part
import { Layout, Divider } from "antd";

// Custom
import {
  HeaderTitle,
  openNotification,
  CustomDrawerButton
} from "../../../../custom";
import * as All from "./";

// Services
import { getUser, addUser } from "../../../../../services/userServices";

const { Content } = Layout;

const PersonalContent = () => {
  // initializing states
  const [data, setData] = useState([]);
  const [visible, setVisible] = useState();
  const [editData, setEditData] = useState({});
  const [drawerVisible, setDrawerVisible] = useState();

  //   hook cycles
  const fetchPersonData = async () => {
    await getUser()
      .then(res => res.data)
      .then(res => setData(res))
      .catch(err =>
        openNotification(
          "error",
          "Cannot get informations",
          "Please check connection"
        )
      );
  };

  useEffect(() => {
    fetchPersonData();
  }, []);

  // custom Drawer
  const showDrawer = () => {
    setDrawerVisible(true);
  };
  const onhandleAdd = async items => {
    try {
      await addUser(items);
    } catch (error) {
      openNotification("error", "Cannot add User", "Check Connection");
    }
  };

  const onClose = () => {
    setDrawerVisible(false);
  };

  // //   Modal handle
  // const onCancel = () => {
  //   setVisible(false);
  // };

  // //   handle actions in table item
  // const onhandleUpdate = async item => {
  //   const orginaldata = { ...item };
  //   try {
  //     await updateDishes(item);
  //   } catch (error) {
  //     setEditData(orginaldata);
  //   }
  // };
  // const deleteItem = async item => {
  //   const orginaldata = data;
  //   try {
  //     await deleteDishes(item);
  //   } catch (error) {
  //     setData(orginaldata);
  //   }
  // };

  // const editItem = async item => {
  //   const newData = { ...item };
  //   setEditData(newData);
  //   setVisible(true);
  // };

  return (
    <Fragment>
      <Content style={{ margin: "2em 16px" }}>
        <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
          <HeaderTitle title={"Personal Menu"} />
          <CustomDrawerButton title="Add Personal" showDrawer={showDrawer} />
          <Divider />
          <All.PersonDarawer
            title="Add Person"
            visible={drawerVisible}
            onClose={onClose}
            onhandleAdd={onhandleAdd}
          />

          <All.PersonalTable
            data={data}
            // deleteItem={deleteItem}
            // editItem={editItem}
          />
          {/* <All.DishModal
            visible={visible}
            onCancel={onCancel}
            data={editData}
            onhandleUpdate={onhandleUpdate}
          /> */}
        </div>
      </Content>
    </Fragment>
  );
};

export default PersonalContent;
