// react
import React, { Component, Fragment } from "react";

// third part
import { Route, Switch, Redirect } from "react-router-dom";

import { SignIn, Admin, Employee, Register, NotFound } from "./components/";

export default class App extends Component {
  state = {
    newUser: {}
  };

  loadUser = data => {
    // let oldData = { ...this.state.newUser };
    // let newData = { ...data };
    // oldData = newData;
    // this.setState({
    //   newUser: oldData
    //   // newUser: {
    //   //   id: data.id,
    //   //   name: data.first_name,
    //   //   salary: data.salary,
    //   //   type: data.work_position,
    //   //   age: data.age,
    //   //   address: data.address,
    //   //   phone: data.phone_number
    //   // }
    // });
  };

  render() {
    // console.log(this.state.newUser);
    return (
      <Fragment>
        <Switch>
          <Route
            path="/admin"
            render={props => <Admin {...props} newUser={this.loadUser} />}
          />
          <Route path="/employee" component={Employee} />
          <Route
            path="/register"
            render={props => <Register {...props} loadUser={this.loadUser} />}
          />
          <Route path="/signin" component={SignIn} />
          <Route path="/not-found" component={NotFound} />
          <Redirect from="/" exact to="/signin" />
          <Redirect to="/not-found" />
        </Switch>
      </Fragment>
    );
  }
}
