// React
import React from "react";

// Third Part
import { Row, Col, Drawer, Form, InputNumber, Input, DatePicker } from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";

const ReservationDrawer = ({ visible, onClose, onHandleAdd }) => {
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };

  return (
    <Drawer
      title="Reserv place"
      visible={visible}
      closable={false}
      placement="bottom"
      onClose={onClose}
    >
      <Form hideRequiredMark onFinish={onHandleAdd}>
        <Row>
          <Col span={5}>
            <Form.Item
              label="Table_number"
              name="table_number"
              rules={[{ required: true, message: "Enter tabel Number" }]}
            >
              <InputNumber className="w-100" />
            </Form.Item>
          </Col>
          <Col span={5} offset={1}>
            <Form.Item
              label="Customer Name"
              name="customer_name"
              rules={[{ required: true, message: "Enter Customer name" }]}
            >
              <Input />
            </Form.Item>
          </Col>
          <Col offset={1}>
            <Form.Item
              label="Date"
              name="date"
              rules={[{ required: true, message: "Choose the Date" }]}
            >
              <DatePicker />
            </Form.Item>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};

export default ReservationDrawer;
