import React, { Component, Fragment } from "react";

import {
  Card,
  Icon,
  Avatar,
  Row,
  Col,
  Drawer,
  Button,
  Input,
  InputNumber,
  Select,
  Divider,
  Modal,
  Typography
} from "antd";

import "../../../assets/css/Order.css";

const { Meta } = Card;
const { Text } = Typography;

const OPTIONS = [" Apples ", " CheeseBurger ", " Bananas ", " FrenchFries "];

export default class Order extends Component {
  state = {
    data: [
      {
        id: 1,
        table_number: 1,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        name: "Alan",
        order: ["bananase"]
      }
    ],
    visible: false,
    modal: false,
    id: 0,
    table_number: "",
    url: "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
    name: "",
    order: []
  };

  showDrawer = () => {
    this.setState({ visible: true });
  };

  onClose = () => {
    this.setState({
      visible: false
    });
  };

  numberChange = number => {
    this.setState({ table_number: number });
  };

  nameChange = event => {
    this.setState({ data: event.target.value });
  };

  orderSelect = value => {
    this.setState({
      order: value
    });
  };

  editModalName = event => {
    this.setState({ modalName: event.target.value });
  };
  editModalOrder = () => {};

  handleOrder = () => {
    const { id, data, table_number, url, name, order } = this.state;
    const newData = {
      id: id,
      table_number: table_number,
      url: url,
      name: name,
      order: order
    };
    if (table_number !== null && name !== "" && order !== "") {
      this.setState({
        data: [...data, newData],
        id: id + 1
      });
    }
  };

  handleDelete = dataId => {
    const data = this.state.data.filter(item => item.id !== dataId);
    this.setState({ data: data });
  };

  handleEdit = () => {
    this.setState({ modal: true });
  };

  render() {
    const { data } = this.state;
    const { order } = this.state;
    const filteredOptions = OPTIONS.filter(o => !order.includes(o));
    return (
      <div
        style={{
          background: "#fff",
          padding: 24,
          minHeight: 680
        }}
      >
        <Row>
          <Button
            type="primary"
            style={{ width: "150px", float: "right" }}
            onClick={this.showDrawer}
          >
            <Icon type="plus" />
            New Order
          </Button>
        </Row>
        <Drawer
          title="Add new Order"
          width={420}
          onClose={this.onClose}
          visible={this.state.visible}
          bodyStyle={{ paddingBottom: 80 }}
        >
          <Row gutter={[40, 20]}>
            <Col span={10}>
              <InputNumber
                placeholder="Enter table number"
                style={{ width: "150px" }}
                value={this.state.table_number}
                onChange={this.numberChange}
              />
            </Col>
            <Col span={10}>
              <Input
                style={{ width: "150px" }}
                placeholder="Enter customer"
                value={this.state.name}
                onChange={this.nameChange}
                allowClear
              />
            </Col>
          </Row>
          <Row gutter={[20, 20]}>
            <Col span={10}>
              <Select
                mode="multiple"
                style={{ width: "100%" }}
                placeholder="Order"
                value={order}
                onChange={this.orderSelect}
              >
                {filteredOptions.map(item => (
                  <Select.Option key={item} value={item}>
                    {item}
                  </Select.Option>
                ))}
              </Select>
            </Col>
          </Row>
          <Row>
            <Divider />
            <Col span={6}>
              <Button
                onClick={() => this.setState({ visible: false })}
                style={{ width: "100px" }}
                type="danger"
              >
                Cancel
              </Button>
            </Col>
            <Col offset={2} span={6}>
              <Button
                style={{ width: "100px" }}
                type="primary"
                onClick={this.handleOrder}
              >
                Order
              </Button>
            </Col>
          </Row>
        </Drawer>
        <Row gutter={2}>
          <Divider />
          {data.map(a => (
            <Fragment>
              <Col span={6}>
                <Card
                  key={a.id}
                  id={a.id}
                  style={{ width: 300, margin: "2em 2em" }}
                  title={a.table_number}
                  cover={<img alt="" src={a.url} />}
                  style={{
                    height: "200px",
                    margin: "1em 1em"
                  }}
                  actions={[
                    <Icon
                      type="delete"
                      onClick={() => this.handleDelete(a.id)}
                    />,
                    <Icon type="edit" onClick={this.handleEdit} />
                  ]}
                >
                  <Meta
                    avatar={<Avatar>{a.name}</Avatar>}
                    title={a.name}
                    description={a.order}
                  />
                </Card>
              </Col>
              <Modal
                visible={this.state.modal}
                onCancel={() => this.setState({ modal: false })}
                onok={console.log("ok")}
              >
                <Row>
                  <Col span={4}>
                    <Text>Name:</Text>
                  </Col>
                  <Col span={6}>
                    <Input
                      value={a.name}
                      onChange={() => this.editModalName(a.name)}
                    />
                  </Col>
                </Row>
                <Row>
                  <Col span={4}>
                    <Text>Order:</Text>
                  </Col>
                  <Col span={6}>
                    <Input value={a.order} onChange={this.editModalOrder} />
                  </Col>
                </Row>
              </Modal>
            </Fragment>
          ))}
        </Row>
      </div>
    );
  }
}
