import React from "react";
import {
  Drawer,
  Input,
  InputNumber,
  Select,
  Button,
  Form,
  Row,
  Col
} from "antd";
import FormItem from "antd/lib/form/FormItem";
import CusomInput from "./input";
const { Option } = Select;
const Drawers = ({
  visible,
  onClose,
  name,
  price,
  namehandleChange,
  priceHandleChange,
  handleAdd,
  typehandleChange,
  ...rest
}) => {
  return (
    <Drawer
      width={420}
      title="AddItem"
      closable={false}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <Form>
        <Form.Item>
          <CusomInput value={name} onChange={namehandleChange} />
        </Form.Item>
        <Form.Item>
          <CusomInput
            placeholder="price"
            price={price}
            onPrice={priceHandleChange}
            // style={{ width: "370px" }}
          />
        </Form.Item>

        <Form.Item>
          <Select onChange={typehandleChange} defaultValue="select type">
            <Option value="snackðŸ¿">Snack</Option>
            <Option value="soupðŸœ">Soup</Option>
            <Option value="desertðŸ®">Desert</Option>
          </Select>
        </Form.Item>
        <Row>
          <Col span={8}>
            <FormItem>
              <Button onClick={onClose} type="danger">
                Cancel
              </Button>
            </FormItem>
          </Col>
          <Col span={8}>
            <Form.Item>
              <Button onClick={handleAdd} type="primary">
                Add
              </Button>
            </Form.Item>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};

export default Drawers;
