import React, { Component, Fragment } from "react";
import _ from "lodash";
import {
  Layout,
  Row,
  Col,
  Form,
  Input,
  InputNumber,
  Divider,
  Button,
  Typography,
  Select,
} from "antd";
import { getUser } from "../../../../services/userServices";
import { name } from "../../../../config.json";
import { openNotification } from "../../../custom";
import UserInfo from "./userInfo";

class Profile extends Component {
  state = { user: [], id: "", first_name: "", last_name: "" };
  async componentDidMount() {
    await getUser()
      .then((res) => res.data)
      .then((data) =>
        this.setState({
          user: _.find(data, ["last_name", localStorage.getItem(name)]),
        })
      );
    this.setState({ id: this.state.user.id });
  }
  handleUpdate = (items) => {
    const { id } = this.state;
    const newData = {
      id: id,
    };
    console.log(newData);
  };
  handleChange = (event) => {
    this.setState({ id: event.target.value });
  };
  render() {
    return (
      <Fragment>
        <UserInfo data={this.state.user} />
        <Input value={this.state.id} onChange={this.handleChange} />
        <Button onClick={this.handleUpdate}>Edit</Button>
      </Fragment>
    );
  }
}

export default Profile;
