import React, { useEffect, useState } from "react";
import { Modal, Form, Row, Col, Select, Input, TimePicker, Button } from "antd";

const layout = {
  labelCol: {
    span: 8
  },
  wrapperCol: {
    span: 16
  }
};
const EditModal = ({ data, handleUpdate }) => {
  return (
    <Modal>
      <Form {...layout} hideRequiredMark onFinish={handleUpdate}>
        <Row>
          <Col span={5}>
            <Form.Item
              label="Table Number"
              name="table_number"
              rules={[{ required: true, message: "Enter Table Number" }]}
            >
              <Select placeholder="select table number">
                {tableNumber.map(table => (
                  <Select.Option key={table.id} value={table.table_number}>
                    {table.table_number}
                  </Select.Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col span={5}>
            <Form.Item
              label="Customer"
              name="customer_name"
              rules={[{ required: true, message: "Enter Customer Name" }]}
            >
              <Input placeholder="Customer Name" allowClear />
            </Form.Item>
          </Col>
          <Col span={5}>
            <Form.Item label="Order" name="dish">
              <Select mode="tags" placeholder="select order">
                {orders.map(orders => (
                  <Select.Option keys={orders.id} value={orders.name}>
                    {orders.name}
                  </Select.Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col span={5}>
            <Form.Item label="Time" name="time" placeholder="select time">
              <TimePicker />
            </Form.Item>
          </Col>
        </Row>
        <Row>
          <Col>
            <Button type="primary" htmlType="submit">
              Order
            </Button>
          </Col>
          <Col>
            <Button type="danger" className="ml-2" onClick={onClose}>
              Cancel
            </Button>
          </Col>
        </Row>
      </Form>
    </Modal>
  );
};

export default EditModal;
{
  data;
}
