// React
import React, { useState, useEffect } from "react";

// third part
import {
  Drawer,
  Form,
  Row,
  Col,
  Select,
  Input,
  TimePicker,
  Button,
  TreeSelect
} from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";
import { getDishes } from "../../../../../services/dishServices";

// custom
import { openNotification } from "../../../../custom";

const layout = {
  labelCol: {
    span: 8,
    offset: 1
  },
  wrapperCol: {
    span: 16
  }
};

const OrderDrawer = ({ visible, onClose, handleAdd }) => {
  const onSelectChange = () => {};

  // state
  const [tableNumber, setTableNumber] = useState([]);
  const [orders, setOrders] = useState([]);

  //   fetchTableNumber
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };
  const fetchOrders = async () => {
    try {
      await getDishes()
        .then(res => res.data)
        .then(data => setOrders(data));
    } catch (error) {}
  };

  useEffect(() => {
    fetchTableNumber();
    fetchOrders();
  }, []);

  return (
    <Drawer
      title="Take Order"
      visible={visible}
      onClose={onClose}
      closable={false}
      placement="bottom"
      width={480}
    >
      <Form {...layout} hideRequiredMark onFinish={handleAdd}>
        <Row>
          <Col xs={24} sm={12} md={12} lg={7}>
            <Form.Item
              label="Table Number"
              name="table_number"
              rules={[{ required: true, message: "Enter Table Number" }]}
            >
              <Select placeholder="select table number">
                {tableNumber.map(table => (
                  <Select.Option key={table.id} value={table.table_number}>
                    {table.table_number}
                  </Select.Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col xs={24} sm={12} md={12} lg={6}>
            <Form.Item
              label="Customer"
              name="customer_name"
              rules={[{ required: true, message: "Enter Customer Name" }]}
            >
              <Input placeholder="Customer Name" allowClear />
            </Form.Item>
          </Col>
          <Col xs={24} sm={12} lg={6}>
            <Form.Item name="order">
              <TreeSelect
                style={{ width: "100%" }}
                dropdownStyle={{ maxHeight: 400, overflow: "auto" }}
                placeholder="Please select"
                allowClear
                multiple
              >
                {orders.map(or => (
                  <TreeSelect.TreeNode value={or.type} title={or.type}>
                    <TreeSelect.TreeNode value={or.name} title={or.name} />
                    <TreeSelect.TreeNode value={or.price} title={or.price} />
                  </TreeSelect.TreeNode>
                ))}
              </TreeSelect>
            </Form.Item>
          </Col>
          <Col xs={24} sm={12} md={12} lg={5}>
            <Form.Item label="Time" name="time" placeholder="select time">
              <TimePicker className="w-100" />
            </Form.Item>
          </Col>
        </Row>
        <Row>
          <Col xs={4} sm={4} md={3} lg={2} xl={{ span: 2, offset: 3 }}>
            <Button type="primary" htmlType="submit">
              Order
            </Button>
          </Col>
          <Col xs={4} sm={3} md={3}>
            <Button type="danger" onClick={onClose}>
              Cancel
            </Button>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};

export default OrderDrawer;
