import React, { Component } from "react";

import {
  Card,
  Icon,
  Avatar,
  Row,
  Col,
  Drawer,
  Button,
  Input,
  InputNumber
} from "antd";

const { Meta } = Card;

export default class Order extends Component {
  state = {
    data: [
      {
        id: 1,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        title: "table1",
        description: "soup"
      },
      {
        id: 2,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        title: "table1",
        description: "soup"
      },
      {
        id: 3,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        title: "table1",
        description: "soup"
      },
      {
        id: 4,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        title: "table1",
        description: "soup"
      },
      {
        id: 5,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        title: "table1",
        description: "soup"
      },
      {
        id: 6,
        url:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        title: "table1",
        description: "soup"
      }
    ],
    visible: false
  };

  showDrawer = () => {
    this.setState({ visible: true });
  };

  onClose = () => {
    this.setState({
      visible: false
    });
  };
  handleDelete = dataId => {
    const data = this.state.data.filter(item => item.id !== dataId);
    this.setState({ data: data });
  };
  render() {
    const { data } = this.state;
    return (
      <div
        style={{
          background: "#fff",
          padding: 24,
          minHeight: 680
        }}
      >
        <Row>
          <Button type="primary" onClick={this.showDrawer}>
            <Icon type="plus" />
            Add new Order
          </Button>
        </Row>
        <Drawer
          title="Add new Order"
          width={520}
          onClose={this.onClose}
          visible={this.state.visible}
          bodyStyle={{ paddingBottom: 80 }}
        >
          <Row>
            <Col span={6}>
              <InputNumber placeholder="Enter table number" />
            </Col>
          </Row>
        </Drawer>
        <Row gutter={2}>
          {data.map(a => (
            <Col span={6}>
              <Card
                key={a.id}
                id={a.id}
                style={{ width: 300, margin: "2em 2em" }}
                title={a.title}
                cover={<img alt="" src={a.url} />}
                actions={[
                  <Icon type="delete" onClick={() => this.handleDelete(a.id)} />
                ]}
              >
                <Meta
                  avatar={
                    <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                  }
                  title={a.title}
                  description={a.description}
                />
              </Card>
            </Col>
          ))}
        </Row>
      </div>
    );
  }
}
