import React, { useEffect, useState } from "react";

import { Modal, Form, Select, Input, DatePicker } from "antd";

import { getTable } from "../../../../../services/tableServices";
import { openNotification } from "../../../../custom";

const layout = {
  labelCol: {
    span: 5
  },
  wrapperCol: {
    span: 16
  }
};

const EditModal = ({ data, visible, onClose, handleUpdate }) => {
  const [form] = Form.useForm();
  const [tableNumber, setTableNumber] = useState([]);

  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };

  useEffect(() => {
    fetchTableNumber();
  }, []);
  return (
    <Modal
      closable={false}
      visible={visible}
      okText="Edit"
      cancelText="Cancel"
      onCancel={onClose}
      onOk={() => {
        form
          .validateFields()
          .then(values => {
            form.resetFields();
            handleUpdate(values);
          })
          .catch(info => {
            console.log("Validate Failed:", info);
          });
      }}
    >
      <Form
        {...layout}
        hideRequiredMark
        onFinish={handleUpdate}
        form={form}
        initialValues={{
          id: data.id,
          table_number: data.table
        }}
      >
        <Form.Item name="id">
          <h1>Edit Orders</h1>
        </Form.Item>
        <Form.Item
          label="Table Number"
          name="table_number"
          rules={[{ required: true, message: "Enter Table Number" }]}
        >
          <Select placeholder="select table number">
            {tableNumber.map(table => (
              <Select.Option key={table.id} value={table.table_number}>
                {table.table_number}
              </Select.Option>
            ))}
          </Select>
        </Form.Item>{" "}
      </Form>
    </Modal>
  );
};

export default EditModal;
