import React from "react";

import { Modal, Form, InputNumber, Typography, DatePicker } from "antd";
const EditModal = ({ visible, data, handleEdit, onClose }) => {
  const [form] = Form.useForm();
  return (
    <Modal
      visible={visible}
      closable={false}
      title="Create a new collection"
      okText="Edit"
      cancelText="Cancel"
      onCancel={onClose}
      onOk={() => {
        form
          .validateFields()
          .then(values => {
            form.resetFields();
            handleEdit(values);
          })
          .catch(info => {
            console.log("Validate Failed:", info);
          });
      }}
    >
      <Form
        form={form}
        onFinish={handleEdit}
        hideRequiredMark
        initialValues={{
          id: data.id,
          table_number: data.table_number,
          seats: data.seats
        }}
      >
        <Form.Item name="id">
          <Typography level={4}> Edit Table items</Typography>
        </Form.Item>

        <Form.Item
          name="table_number"
          label="Tabel Number"
          rules={[{ required: true, message: "Enter table number" }]}
        >
          <InputNumber min={0} className="w-100" />
        </Form.Item>
        <Form.Item
          label="Seats"
          name="seats"
          rules={[{ required: true, message: "Enter seats" }]}
        >
          <InputNumber min={0} className="w-100" />
        </Form.Item>
        <Form.Item
          label="Date"
          name="date"
          rules={[{ required: true, message: "Enter the date" }]}
        >
          <DatePicker />
        </Form.Item>
      </Form>
    </Modal>
  );
};

export default EditModal;
