import React, { Fragment } from "react";

import { Layout, Typography, Button } from "antd";

const { Content } = Layout;
const { Title, Paragraph, Text } = Typography;
export default function Edit(props) {
  // id: a.id,
  //                     title: a.table_number,
  //                     url: a.url,
  //                     name: a.name,
  //                     order: a.order
  return (
    <Fragment>
      <Layout className="layout">
        <Content style={{ padding: "0 50px" }}>
          <div className="container">
            <img alt="example" src={props.location.state.url} />

            <div>
              <Title level={4}>{props.location.state.title}</Title>
            </div>
            <div>
              <Paragraph style={{ width: "90%" }}>
                {props.location.state.content}
              </Paragraph>
            </div>
            <div>
              <Text>{props.location.state.author}</Text>
            </div>

            <Button
              style={{ margin: "1em 0" }}
              type="primary"
              onClick={() => props.history.push("/order")}
            >
              Back to
            </Button>
          </div>
        </Content>
      </Layout>
    </Fragment>
  );
}
