import React, { Fragment } from "react";

const tableColumn = (name, price, type, date, deleteItem, editItem) => {
  const columns = [
    {
      title: name,
      dataIndex: name
    },
    {
      title: price,
      dataIndex: price
    },
    {
      title: type,
      dataIndex: type
    },
    {
      title: date,
      dataIndex: date
    },
    {
      title: title,
      dataIndex: dataindex,
      render: (text, record) => (
        <Fragment>
          <Button
            type="danger"
            size="small"
            style={{ width: "60px", margin: " 0 1em" }}
            onClick={() => deleteItem(record.id)}
          >
            Delete
          </Button>
          <Button
            type="primary"
            size="small"
            style={{ width: "60px" }}
            onClick={() => editItem(record.id)}
          >
            Edit
          </Button>
        </Fragment>
      )
    }
  ];
};
export { tableColumn };
