// React
import React, { useEffect, useState } from "react";

// third part
import { Modal, Form, Row, Col, Select, Input, TimePicker } from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";
import { getDishes } from "../../../../../services/dishServices";
// custom
import { openNotification } from "../../../../custom";

const layout = {
  labelCol: {
    span: 8
  },
  wrapperCol: {
    span: 16
  }
};
const EditModal = ({ editableData, visible, handleUpdate, onClose }) => {
  const [form] = Form.useForm();
  const [tableNumber, setTableNumber] = useState([]);
  const [orders, setOrders] = useState([]);
  console.log(editableData);
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };
  const fetchOrders = async () => {
    try {
      await getDishes()
        .then(res => res.data)
        .then(data => setOrders(data));
    } catch (error) {}
  };
  useEffect(() => {
    fetchTableNumber();
    fetchOrders();
  }, []);

  return (
    <Modal
      closable={false}
      visible={visible}
      okText="Edit"
      cancelText="Cancel"
      onCancel={onClose}
      onOk={() => {
        form
          .validateFields()
          .then(values => {
            form.resetFields();
            handleUpdate(values);
          })
          .catch(info => {
            console.log("Validate Failed:", info);
          });
      }}
    >
      <Form
        {...layout}
        hideRequiredMark
        onFinish={handleUpdate}
        form={form}
        initialValues={{
          id: editableData.id
        }}
      >
        <Form.Item name="id" />
        <Form.Item
          label="Table Number"
          name="table_number"
          rules={[{ required: true, message: "Enter Table Number" }]}
        >
          <Select
            defaultValue={editableData.table_number}
            placeholder="select table number"
          >
            {tableNumber.map(table => (
              <Select.Option key={table.id} value={table.table_number}>
                {table.table_number}
              </Select.Option>
            ))}
          </Select>
        </Form.Item>
        <Form.Item
          label="Customer"
          name="customer_name"
          rules={[{ required: true, message: "Enter Customer Name" }]}
        >
          <Input placeholder="Customer Name" allowClear />
        </Form.Item>
        <Form.Item label="Order" name="dish">
          <Select mode="tags" placeholder="select order">
            {orders.map(orders => (
              <Select.Option keys={orders.id} value={orders.name}>
                {orders.name}
              </Select.Option>
            ))}
          </Select>
        </Form.Item>
        <Form.Item label="Time" name="time" placeholder="select time">
          <TimePicker />
        </Form.Item>
      </Form>
    </Modal>
  );
};

export default EditModal;
