import React, { useState, useEffect, Fragment } from "react";

import _ from "lodash";

import { openNotification } from "../../../../custom/";

import { getOrder } from "../../../../../services/orderServices";

const SearchOrders = () => {
  // state
  const [orderData, setOrderData] = useState([]);
  const [searchOrder, setSearch] = useState({});

  // getOrders
  const getData = async () => {
    try {
      await getOrder()
        .then(res => res.data)
        .then(data => setOrderData(data));
    } catch (err) {
      openNotification("error", "Can't get data", "Check connection");
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const onSearch = value => {
    const name = _.find(orderData, ["table", value]);
    const newData = { ...name };
    setSearch(newData);
  };
  return (
    <Fragment>
      <InputNumber
        className="w-100"
        onChange={value => onSearch(value)}
        // prefix={}
      />
      <Divider />
      <Card
        className="border border-warning rounded m-2"
        title={
          <p className="bg-light text-dark border border-dark rounded text-center ">{`Table: ${searchOrder.table}`}</p>
        }
        cover={
          <div className="container">
            <Typography.Title
              level={2}
              className="text-center text-uppercase font-weight-light mt-4 text-secondary"
            >
              {searchOrder.customer}
              <Divider className="mt-4" />
            </Typography.Title>
          </div>
        }
      >
        <Card.Meta
          title={
            <Typography.Paragraph ellipsis={{ rows: 1, expandable: true }}>
              {`Orders: ${searchOrder.ordered_items}`}
            </Typography.Paragraph>
          }
          description={`Time: ${searchOrder.time}`}
        />
      </Card>
      ;
    </Fragment>
  );
};

export default SearchOrders;
