// React
import React, { useState, useEffect } from "react";

// Third Part
import { Row, Col, Drawer, Form, Select, Input, DatePicker } from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";
import { openNotification } from "../../../../custom";

const ReservationDrawer = ({ visible, onClose, onHandleAdd }) => {
  const [tableNumber, setTableNumber] = useState([]);
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };

  useEffect(() => {
    fetchTableNumber();
  }, []);

  return (
    <Drawer
      title="Reserv place"
      visible={visible}
      closable={false}
      placement="bottom"
      onClose={onClose}
    >
      <Form hideRequiredMark onFinish={onHandleAdd}>
        <Row>
          <Col span={5}>
            <Form.Item
              label="Table_number"
              name="table_number"
              rules={[{ required: true, message: "Enter tabel Number" }]}
            >
              <Select></Select>
            </Form.Item>
          </Col>
          <Col span={5} offset={1}>
            <Form.Item
              label="Customer Name"
              name="customer_name"
              rules={[{ required: true, message: "Enter Customer name" }]}
            >
              <Input />
            </Form.Item>
          </Col>
          <Col offset={1}>
            <Form.Item
              label="Date"
              name="date"
              rules={[{ required: true, message: "Choose the Date" }]}
            >
              <DatePicker />
            </Form.Item>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};

export default ReservationDrawer;
