import React from "react";
import { Drawer, Card, Divider, Input, Typography, InputNumber } from "antd";
const SearchOrder = ({ visible, onSearch, onClose, searchOrder }) => {
  return (
    <Drawer
      title="Search Order"
      closable={false}
      visible={visible}
      onClose={onClose}
      width={400}
    >
      <InputNumber />
      <Divider />
      <Card
        className="border border-warning rounded m-2"
        title={
          <p className="bg-light text-dark border border-dark rounded text-center ">{`Table: ${searchOrder.table}`}</p>
        }
        cover={
          <div className="container">
            <Typography.Title
              level={2}
              className="text-center text-uppercase font-weight-light mt-4 text-secondary"
            >
              {searchOrder.customer}
              <Divider className="mt-4" />
            </Typography.Title>
          </div>
        }
      >
        <Card.Meta
          title={
            <Typography.Paragraph ellipsis={{ rows: 1, expandable: true }}>
              {`Orders: ${searchOrder.ordered_items}`}
            </Typography.Paragraph>
          }
          description={`Time: ${searchOrder.time}`}
        />
      </Card>
    </Drawer>
  );
};

export default SearchOrder;
