import React from "react";
import {
  Drawer,
  Input,
  InputNumber,
  Select,
  Button,
  Form,
  Row,
  Col
} from "antd";

const { Option } = Select;
const Drawers = ({
  visible,
  onClose,
  name,
  price,
  age,
  address,
  namehandleChange,
  priceHandleChange,
  typehandleChange,
  agehandleChange,
  addresshandleChange
}) => {
  return (
    <Drawer
      width={420}
      title="AddItem"
      closable={false}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <Row gutter={20}>
        <Col span={3}>
          <Input
            placeholder="Name"
            value={name}
            onChange={namehandleChange}
            allowClear
          />
        </Col>
        <Col span={3}>
          <InputNumber
            placeholder="Salary"
            value={price}
            onChange={priceHandleChange}
            style={{ width: "150px" }}
          />
        </Col>
        <Col span={3}>
          <Select
            onChange={typehandleChange}
            defaultValue="Select"
            style={{ width: "150px" }}
          >
            <Option value="Waiter">Waiter</Option>
            <Option value="Chef">Chef</Option>
            <Option value="Employee">Employee</Option>
          </Select>
        </Col>
        <Col span={3}>
          <InputNumber
            placeholder="age"
            value={age}
            onChange={agehandleChange}
            style={{ width: "150px" }}
            max={11}
          />
        </Col>
        <Col span={3}>
          <Input
            placeholder="Address"
            value={address}
            onChange={addresshandleChange}
            allowClear
          />
        </Col>
        <Col span={3}>
          <InputNumber
            style={{ width: "150px" }}
            placeholder="Phone"
            value={phone}
            onChange={this.phoneHandleChange}
            max={11}
          />
        </Col>

        <Col span={3}>
          <Button
            onClick={this.handleAdd}
            type="primary"
            style={{ marginBottom: 16, width: "100px" }}
          >
            Add
          </Button>
        </Col>
      </Row>
    </Drawer>
  );
};

export default Drawers;
