import https from "./httpService";
import { apiUrl } from "../config.json";

export function getDishes() {
  return https.get(apiUrl + "dish");
}

export function addDishes(values) {
  return https.post(apiUrl + "dish/add", {
    name: values.name,
    price: values.price,
    type: values.type
  });
}

export function deleteDishes(id) {
  https
    .delete(apiUrl + "dish/delete", { id: id })
    .then(response => id.splice(id));
}
