import React, { Fragment } from "react";
import { Form, Input, Modal, InputNumber } from "antd";

const CustomModal = ({
  visible,
  data,
  handleOk,
  onCancel,
  onNameChange,
  onOrderChange
}) => {
  return (
    <Modal
      title="Edit info"
      visible={visible}
      onOk={() => handleOk(data)}
      onCancel={onCancel}
    >
      {data === null ? (
        console.log("error")
      ) : (
        <Fragment>
          <Form>
            <Form.Item label="Table Number">
              <InputNumber value={data.table_number} onChange={onNameChange} />
            </Form.Item>
            <Form.Item label="Name">
              <Input value={data.name} onChange={onNameChange} />
            </Form.Item>

            <Form.Item label="Orders:">
              <Input value={data.order} onChange={onOrderChange} />
            </Form.Item>
          </Form>
        </Fragment>
      )}
    </Modal>
  );
};

export default CustomModal;
