// React
import React, { Component, Fragment } from "react";

// Third Part
import https from "../../services/httpService";
import { apiUrl } from "../../config.json";
import {
  Form,
  Input,
  Select,
  Button,
  Typography,
  Row,
  Col,
  DatePicker
} from "antd";

// custom
import "../../assets/css/RegisterForm.css";

const { Option } = Select;
const { Title } = Typography;

class SignUp extends Component {
  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      console.log(values);
      // if (!err)
      //   https
      //     .post(apiUrl + "register", {
      //       first_name: values.first_name,
      //       last_name: values.last_name,
      //       gender: values.gender,
      //       address: values.address,
      //       email: values.email,
      //       password: values.password,
      //       phone_number: values.phone,
      //       work_position: values.work_position,
      //       age: values.age,
      //       salary: values.salary,
      //       date: new Date()
      //     })
      //     .then(user => {
      //       if (user) {
      //         this.props.loadUser(user.data);
      //       }
      //     })
      //     .then(this.props.history.replace("/"));
    });
  };
  render() {
    const { getFieldDecorator } = this.props.form;

    return (
      <Fragment>
        <Form onSubmit={this.handleSubmit} className="form">
          <Title style={{ textAlign: "center" }}>Register</Title>
          <Row gutter={20}>
            <Col span={8} offset={5}>
              <Form.Item label={<span>First Name&nbsp;</span>}>
                {getFieldDecorator("first_name", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your first name!",
                      whitespace: true
                    }
                  ]
                })(<Input />)}
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item label={<span>Last Name&nbsp;</span>}>
                {getFieldDecorator("last_name", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your last name!",
                      whitespace: true
                    }
                  ]
                })(<Input />)}
              </Form.Item>
            </Col>
          </Row>
          <Row gutter={20}>
            <Col span={8} offset={5}>
              <Form.Item label="Gender">
                {getFieldDecorator("gender", {
                  rules: [
                    { required: true, message: "Please select your gender!" }
                  ]
                })(
                  <Select placeholder="Select a option ">
                    <Option value="Male">Male</Option>
                    <Option value="Female">Female</Option>
                  </Select>
                )}
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item label="Address">
                {getFieldDecorator("address", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your address!",
                      whitespace: true
                    },
                    {
                      required: true,
                      message: "Please input your Address!"
                    }
                  ]
                })(<Input />)}
              </Form.Item>
            </Col>
          </Row>
          <Row gutter={20}>
            <Col span={8} offset={5}>
              <Form.Item label="E-mail">
                {getFieldDecorator("email", {
                  rules: [
                    {
                      type: "email",
                      message: "The input is not valid E-mail!"
                    },
                    {
                      required: true,
                      message: "Please input your E-mail!"
                    }
                  ]
                })(<Input />)}
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item label="Password" hasFeedback>
                {getFieldDecorator("password", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your password!"
                    }
                  ]
                })(<Input.Password />)}
              </Form.Item>
            </Col>
          </Row>
          <Row gutter={20}>
            <Col span={8} offset={5}>
              <Form.Item label="Phone">
                {getFieldDecorator("phone", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your phone!"
                    }
                  ]
                })(<Input />)}
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item label="Work Position">
                {getFieldDecorator("work_position", {
                  rules: [
                    {
                      required: true,
                      message: "Please select your work position!"
                    }
                  ]
                })(
                  <Select placeholder="Select a option ">
                    <Option value="Chef">Chef</Option>
                    <Option value="Waiter">Waiter</Option>
                    <Option value="Employee">Employee</Option>
                  </Select>
                )}
              </Form.Item>
            </Col>
          </Row>
          <Row gutter={20}>
            <Col span={8} offset={5}>
              <Form.Item label="Age">
                {getFieldDecorator("age", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your age!"
                    }
                  ]
                })(<Input />)}
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item label="Salary">
                {getFieldDecorator("salary", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your salary!"
                    }
                  ]
                })(<Input />)}
              </Form.Item>
            </Col>
          </Row>
          <Form.Item label="date">
            {getFieldDecorator("date", {
              rules: [
                {
                  required: true,
                  message: "Please input your date!"
                }
              ]
            })(<DatePicker showTime />)}
          </Form.Item>
          <Row gutter={20}>
            <Col offset={5}>
              <Form.Item>
                <Button type="primary" htmlType="submit">
                  Register
                </Button>
              </Form.Item>
            </Col>
          </Row>
        </Form>
      </Fragment>
    );
  }
}

const WrappedRegistrationForm = Form.create({ name: "register" })(SignUp);

export default WrappedRegistrationForm;
