import React from "react";

import { Drawer, Input, InputNumber, Button, Form, Row, Col } from "antd";

const ReservationDrawer = ({
  visible,
  userName,
  tableNumber,
  onClose,
  onNameChange,
  onNumberChange
}) => {
  return (
    <Drawer
      title="Reservation"
      width={420}
      closable={false}
      visible={visible}
      onClose={onClose}
    >
      <Form>
        <Form.Item label="Enter Customer name">
          <Input
            placeholder="Enter name"
            value={userName}
            onChange={onNameChange}
          />
        </Form.Item>
        <Form.Item label="Enter Table Number">
          <InputNumber
            placeholder="Enter Table number"
            value={tableNumber}
            onChange={onNumberChange}
            style={{ width: "370px" }}
          />
        </Form.Item>
        <Row>
          <Col span={8}>
            <Button>Add</Button>
          </Col>
          <Col span={8}>
            <Button type="danger" onClick={() => onClose}>
              Cancel
            </Button>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};

export default ReservationDrawer;
