// React
import React, { useState, useEffect } from "react";

// third part
import {
  Drawer,
  Form,
  Row,
  Col,
  Select,
  Input,
  TimePicker,
  Button
} from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";
import { getDishes } from "../../../../../services/dishServices";

// custom
import { openNotification } from "../../../../custom";

const { Option } = Select;

const layout = {
  labelCol: {
    span: 8
  },
  wrapperCol: {
    span: 16
  }
};

const OrderDrawer = ({ visible, onClose }) => {
  // state
  const [tableNumber, setTableNumber] = useState([]);
  const [orders, setOrders] = useState([]);

  //   fetchTableNumber
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };
  const fetchOrders = async () => {
    try {
      await getDishes()
        .then(res => res.data)
        .then(data => setOrders(data));
    } catch (error) {}
  };

  useEffect(() => {
    fetchTableNumber();
    fetchOrders();
  }, []);

  return (
    <Drawer
      title="Take Order"
      visible={visible}
      onClose={onClose}
      closable={false}
      placement="bottom"
      width={480}
    >
      <Form {...layout} hideRequiredMark>
        <Row>
          <Col span={5}>
            <Form.Item
              label="Table Number"
              name="table_number"
              rules={[{ required: true, message: "Enter Table Number" }]}
            >
              <Select placeholder="select table number">
                {tableNumber.map(table => (
                  <Option value={table.table_number}>
                    {table.table_number}
                  </Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col span={5}>
            <Form.Item
              label="Customer"
              name="customer_name"
              rules={[{ required: true, message: "Enter Customer Name" }]}
            >
              <Input placeholder="Customer Name" allowClear />
            </Form.Item>
          </Col>
          <Col span={5}>
            <Form.Item label="Order" name="ordered_items">
              <Select mode="tags" placeholder="select order">
                {orders.map(orders => (
                  <Option value={orders.name}>{orders.name}</Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col span={5}>
            <Form.Item label="Time" name="time" placeholder="select time">
              <TimePicker />
            </Form.Item>
          </Col>
        </Row>
        <Row>
          <Col span={2}>
            <Button type="primary" htmlType="submit">
              Order
            </Button>
          </Col>
          <Col span={1}>
            <Button type="danger" onClick={onClose}>
              Cancel
            </Button>
          </Col>
        </Row>
      </Form>
    </Drawer>
  );
};

export default OrderDrawer;
