// react
import React, { Component, Fragment } from "react";

// third part
import { Layout, Menu, Icon } from "antd";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

// components
import { Dish, Personal, Table } from "./components/";

// custom
import "../../assets/css/AdminForm.css";

// Icons
import { Breakfast, PersonalIcon, TableInfo } from "../../assets/";

const { Header, Content, Footer, Sider } = Layout;

class AdminBoard extends Component {
  state = {
    collapsed: false
  };

  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed
    });
  };
  render() {
    const { history } = this.props;
    return (
      <Fragment>
        <Router>
          <Layout style={{ minHeight: "100vh" }}>
            <Sider trigger={null} collapsible collapsed={this.state.collapsed}>
              <div className="logo">
                <p style={{ textAlign: "center", fontSize: "20px" }}>Admin</p>
              </div>

              <Menu theme="light" mode="inline">
                <Menu.Item key="1">
                  <Breakfast />
                  <span className="menu-title">Dish information</span>
                  <Link to="/dish" />
                </Menu.Item>
                <Menu.Item key="2">
                  <PersonalIcon />
                  <span className="menu-title">Personal Information</span>
                  <Link to="/personal" />
                </Menu.Item>

                <Menu.Item key="3">
                  <TableInfo />
                  <span className="menu-title">Table Information</span>
                  <Link to="/table" />
                </Menu.Item>
                <Menu.Item>
                  <Icon
                    type="link"
                    size="small"
                    // style={{ width: "150px", float: "right" }}
                    onClick={() => history.replace("/")}
                  >
                    Logout
                  </Icon>
                </Menu.Item>
              </Menu>
            </Sider>
            <Layout>
              <Header style={{ background: "#000000", padding: 0 }}>
                <Icon
                  className="trigger"
                  type={this.state.collapsed ? "menu-unfold" : "menu-fold"}
                  onClick={this.toggle}
                />
              </Header>
              <Content style={{ margin: "2em 16px" }}>
                <Route exact path="/dish" component={Dish} />
                <Route exact path="/personal" component={Personal} />
                <Route exact path="/table" component={Table} />
              </Content>
              <Footer style={{ textAlign: "center" }}>
                PLOV ©2020 Created by Arian
              </Footer>
            </Layout>
          </Layout>
        </Router>
      </Fragment>
    );
  }
}
export default AdminBoard;
