import React from "react";
import { Row, Col, Form, Input, InputNumber } from "antd";

const CustomizedForm = ({
  form,
  data,
  firstData,
  secondData,
  thirdData,
  fourthData
}) => (
  <Form
    form={form}
    layout="vertical"
    name="form_in_modal"
    initialValues={{
      id: data.id,
      name: data.name,
      price: data.price,
      type: data.type,
      date: data.date
    }}
  >
    <Row>
      <Col span={15}>
        <Form.Item
          name={firstData}
          label={firstData}
          rules={[
            {
              required: true,
              message: `Please input the ${firstData}!`
            }
          ]}
        >
          <Input />
        </Form.Item>
      </Col>
      <Col span={5} offset={2}>
        <Form.Item
          name={secondData}
          label={secondData}
          rules={[
            {
              required: true,
              message: `Please input the ${secondData}!`
            }
          ]}
        >
          <InputNumber
            min={0}
            step={0.1}
            formatter={secondData => `¥ ${secondData}`}
          />
        </Form.Item>
      </Col>
    </Row>
    <Form.Item
      name={thirdData}
      label={thirdData}
      rules={[
        {
          required: true,
          message: `Please input the ${thirdData}!`
        }
      ]}
    >
      <Input />
    </Form.Item>
  </Form>
);
export default CustomizedForm;
