// React
import React, { useState, useEffect } from "react";

// Third Part
import { Layout, Input, Divider, Row, Col, Card, Typography } from "antd";
import _ from "lodash";

// custom
import { openNotification } from "../../../custom";

// services
import { getReservation } from "../../../../services/reservationServices";

const SearchReservation = () => {
  // state
  const [reserv, searchReserv] = useState([]);
  const [findedData, setfindedData] = useState([]);

  // get Table
  const fetchReservation = async () => {
    await getReservation()
      .then(res => res.data)
      .then(data => searchReserv(data));
  };

  // hook
  useEffect(() => {
    fetchReservation();
  }, []);

  // searchMethod
  const onSearchItem = value => {
    const name = value.currentTarget.value;
    const customer = _.filter(reserv, ["customer_name", name]);
    const newData = [...customer];
    setfindedData(newData);
  };

  return (
    <Layout.Content
      style={{ margin: "2em 16px" }}
      className="border border-danger"
    >
      <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
        <Input
          onChange={value => onSearchItem(value)}
          placeholder="Enter Customer Name"
          className="w-25"
        />
        <div className="w-25">
          <Divider />
        </div>
        <Row>
          {findedData.map(f => (
            <Col key={f.id} span={6}>
              <Card
                key={f.id}
                className="text-center border border-warning rounded m-2"
                title={
                  <Typography.Title
                    className="bg-light border border-dark rounded"
                    level={4}
                  >{`Table: ${f.table_number}`}</Typography.Title>
                }
                cover={
                  <Typography.Title
                    level={3}
                    className="font-weight-light text-uppercase mt-5"
                  >
                    {f.customer_name}
                  </Typography.Title>
                }
              >
                <Divider />
                <Card.Meta
                  title={`Reserved Seats: ${f.seats}`}
                  description={
                    <Typography.Text
                      level={4}
                    >{`Date:${f.date}`}</Typography.Text>
                  }
                />
              </Card>
            </Col>
          ))}
        </Row>
      </div>
    </Layout.Content>
  );
};

export default SearchReservation;
