// React
import React, { useEffect, useState, Fragment } from "react";

// Third part
import { Layout, Divider } from "antd";

import { Order } from "../../../../../services/tableServices";

// Custom
import { HeaderTitle, CustomDrawerButton } from "../../../../custom/";
import * as All from "./";

const { Content } = Layout;
const OrderContent = () => {
  // state
  const [DrawerVisible, setDrawerVisible] = useState();

  // showDrawer
  const showDrawer = () => {
    setDrawerVisible(true);
  };
  const onClose = () => {
    setDrawerVisible(false);
  };
  const handleAdd = async items => {
    await Order(items);
  };

  return (
    <Fragment>
      <Content style={{ margin: "2em 16px" }}>
        <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
          <HeaderTitle title={"Order Menu"} />
          <CustomDrawerButton title={"Take order"} showDrawer={showDrawer} />
          <Divider />
          <All.OrderDrawer
            visible={DrawerVisible}
            onClose={onClose}
            handleAdd={handleAdd}
          />
        </div>
      </Content>
    </Fragment>
  );
};

export default OrderContent;
