import React from "react";
import { Form, Input, Button, Row, Col, InputNumber, DatePicker } from "antd";

const layout = {
  labelCol: { span: 3 },
  wrapperCol: { span: 16 }
};
const tailLayout = {
  wrapperCol: { offset: 8, span: 16 }
};

const CusomizedForm = ({
  firsItem,
  secondItem,
  thirdItem,
  fourthItem,
  onClose,
  onhandleAdd
}) => {
  const onFinish = values => {
    console.log("Success:", values);
  };

  const onFinishFailed = errorInfo => {
    console.log("Failed:", errorInfo);
  };

  return (
    <Form
      {...layout}
      name="basic"
      onFinish={onhandleAdd}
      onFinishFailed={onFinishFailed}
      hideRequiredMark
      className="m-0"
    >
      <Form.Item
        label={firsItem}
        name={firsItem}
        rules={[{ required: true, message: `Please input ${firsItem}` }]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label={secondItem}
        name={secondItem}
        rules={[{ required: true, message: `Please input ${secondItem}` }]}
      >
        <InputNumber
          min={0}
          step={0.1}
          formatter={secondItem => `¥ ${secondItem}`}
        />
      </Form.Item>
      <Form.Item
        label={thirdItem}
        name={thirdItem}
        rules={[{ required: true, message: `Please input ${thirdItem}` }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        label={fourthItem}
        name={fourthItem}
        rules={[{ required: true, message: `Please input ${fourthItem}` }]}
      >
        <DatePicker />
      </Form.Item>

      <Row>
        <Col span={5}>
          <Form.Item {...tailLayout}>
            <Button type="primary" htmlType="submit">
              Add
            </Button>
          </Form.Item>
        </Col>
        <Col span={5}>
          <Form.Item {...tailLayout}>
            <Button type="danger" onClick={onClose}>
              Cancel
            </Button>
          </Form.Item>
        </Col>
      </Row>
    </Form>
  );
};

export default CusomizedForm;
