// React
import React, { useState, useEffect } from "react";

// third part
import { Drawer, Form, Select } from "antd";

// Services
import { getTable } from "../../../../../services/tableServices";

// custom
import { openNotification } from "../../../../custom";

const { Option } = Select;

const OrderDrawer = ({ visible, onClose }) => {
  // state
  const [tableNumber, setTableNumber] = useState([]);

  //   fetchData
  const fetchTableNumber = async () => {
    try {
      await getTable()
        .then(res => res.data)
        .then(data => setTableNumber(data));
    } catch (error) {
      openNotification("error", "Cant get tables", "Check connection");
    }
  };

  useEffect(() => {
    fetchTableNumber();
  }, []);

  return (
    <Drawer visible={visible} onClose={onClose} closable={false}>
      <Form>
        <Form.Item
          label="Table Number"
          name="table_number"
          rules={[{ required: true, message: "Enter Table Number" }]}
        >
          <Select placeholder="select table number"></Select>
        </Form.Item>
      </Form>
    </Drawer>
  );
};

export default OrderDrawer;
