// react
import React, { Component, Fragment } from "react";

// third part
import { Layout, Menu } from "antd";
import { Route, Link } from "react-router-dom";

// custom
import "../../assets/css/style.css";
import Breakfast from "../../assets/icons/Breakfast";
import Personal from "../../assets/icons/Personal";
import TableInfo from "../../assets/icons/Table_Info";
import Dish from "./components/Dish";
import Table from "./components/Table";

const { Header, Content, Footer, Sider } = Layout;

export default class AdminBoard extends Component {
  state = {
    collapsed: false
  };

  onCollapse = collapsed => {
    console.log(collapsed);
    this.setState({ collapsed });
  };

  render() {
    return (
      <Fragment>
        <Layout style={{ minHeight: "100vh" }}>
          <Sider
            collapsible
            collapsed={this.state.collapsed}
            onCollapse={this.onCollapse}
          >
            <div className="logo" />

            <Menu theme="light" defaultSelectedKeys={["1"]} mode="inline">
              <Menu.Item key="1">
                <Breakfast />
                <span className="menu-title">Dish information</span>
                <Link to="/dish"></Link>
              </Menu.Item>
              <Menu.Item key="2">
                <Personal />
                <span className="menu-title">Personal Information</span>
                <Link to="/personal"></Link>
              </Menu.Item>

              <Menu.Item key="9">
                <TableInfo />
                <span className="menu-title">Table Information</span>
                <Link to="/table"></Link>
              </Menu.Item>
            </Menu>
          </Sider>
          <Layout>
            <Header style={{ background: "#000000", padding: 0 }} />
            <Content style={{ margin: "2em 16px" }}>
              <Route path="/dish/" component={Dish} />
              <Route path="/personal/" component={Personal} />
              <Route path="/table/" component={Table} />
            </Content>
            <Footer style={{ textAlign: "center" }}>
              Ant Design ©2018 Created by Ant UED
            </Footer>
          </Layout>
        </Layout>
      </Fragment>
    );
  }
}
