// react
import React, { Component, Fragment } from "react";

// third part
import { Route, Switch, Redirect } from "react-router-dom";

import { Dish, Personal, Table } from "./components/Admin/components";
import { SignIn, Admin, Employee, Register, NotFound } from "./components/";

export default class App extends Component {
  state = {
    newUser: {}
  };

  loadUser = data => {
    const newdata = { ...data };
    this.setState({ newUser: newdata });
  };

  render() {
    const { newUser } = this.state;
    console.log(newUser);
    return (
      <Fragment>
        <Switch>
          <Route
            path="/admin"
            render={props => <Admin {...props} newUser={newUser} />}
          />
          <Route path="/employee" component={Employee} />
          <Route
            path="/register"
            render={props => <Register {...props} loadUser={this.loadUser} />}
          />
          <Route path="/signin" component={SignIn} />
          <Route exact path="/dish" component={Dish} />
          <Route exact path="/personal" component={Personal} />
          <Route exact path="/table" component={Table} />
          <Route path="/not-found" component={NotFound} />
          <Redirect from="/" exact to="/signin" />
          <Redirect to="/not-found" />
        </Switch>
      </Fragment>
    );
  }
}
