import React, { useState, Fragment } from "react";
import { PrinterOutlined } from "@ant-design/icons";
import Pdf from "react-to-pdf";
import { Link } from "react-router-dom";
import _ from "lodash";
import { Layout, Typography, Row, Col, Divider } from "antd";

const ref = React.createRef();
const option = {
  unit: "mm",
  format: [600, 350],
};

const PrintBill = ({ location }) => {
  const [data] = useState(location.state);
  const [newdata] = useState({ ...data });

  return (
    <Fragment>
      <Layout.Content style={{ margin: "2em 16px" }}>
        <div
          style={{ background: "#fff", padding: 24, minHeight: 280 }}
          className="border border-dark rounded"
        >
          <div
            style={{ width: 400, height: 500 }}
            className="container border border-warning rounded mt-4"
            ref={ref}
          >
            <div className="bg-light border border-dark rounded p-2 mt-3">
              <Row>
                <Col span={10}>
                  <div className="mt-2">
                    <Typography.Title>{`Table: ${newdata.table}`}</Typography.Title>
                  </div>
                </Col>
                <Col span={2}>
                  <Divider
                    type="vertical"
                    className="bg-dark"
                    style={{ height: "100px" }}
                  />
                </Col>
                <Col span={10}>
                  <div className="mt-2">
                    <Typography.Title
                      level={3}
                    >{`Customer: ${newdata.customer}`}</Typography.Title>
                  </div>
                </Col>
              </Row>
            </div>
            <Divider type="horizontal" />
            <ul className="bg-light p-3 border border-dark rounded">
              <Row>
                <Col span={8}>
                  {newdata.orders.map((order) => (
                    <li style={{ listStyleType: "none" }}>{order}</li>
                  ))}
                </Col>

                <Col span={5}>
                  {newdata.price.map((price) => (
                    <li style={{ listStyleType: "none" }}>
                      ................................{price}¥
                    </li>
                  ))}
                </Col>
              </Row>
            </ul>
            <Divider />
            <div className="text-center">
              <Typography.Title
                level={3}
                className="border border-info rounded bg-light "
              >{`Total Price: ${_.sum(newdata.price)}¥`}</Typography.Title>
            </div>
          </div>
          <div className="text-center mt-5">
            <Pdf
              targetRef={ref}
              filename={`Table-${newdata.table}.pdf`}
              options={option}
              x={8}
              y={6}
            >
              {({ toPdf }) => (
                <PrinterOutlined style={{ fontSize: "30px" }} onClick={toPdf}>
                  Print
                </PrinterOutlined>
              )}
            </Pdf>
            <Link className="btn btn-primary m-5" to="/info">
              Back
            </Link>
          </div>
        </div>
      </Layout.Content>
    </Fragment>
  );
};
export default PrintBill;
