import React from "react";
import { Drawer, Input, InputNumber, Select, Button, Form } from "antd";
const { Option } = Select;
const Drawers = ({
  visible,
  onClose,
  name,
  price,
  namehandleChange,
  priceHandleChange,
  handleAdd,
  typehandleChange
}) => {
  const { getFieldDecorator } = this.form;
  return (
    <Drawer
      width={420}
      title="Basic Drawer"
      closable={false}
      placement="right"
      onClose={onClose}
      visible={visible}
    >
      <Form>
        <Form.Item>
          <Input
            placeholder="name"
            value={name}
            onChange={namehandleChange}
            allowClear
          />
        </Form.Item>
        <Form.Item>
          <InputNumber
            placeholder="price"
            value={price}
            onChange={priceHandleChange}
          />
        </Form.Item>

        <Form.Item>
          <Select onChange={typehandleChange} defaultValue="select type">
            <Option value="snackðŸ¿">Snack</Option>
            <Option value="soupðŸœ">Soup</Option>
            <Option value="desertðŸ®">Desert</Option>
          </Select>
        </Form.Item>

        <Form.Item>
          <Button onClick={handleAdd} type="primary">
            Add
          </Button>
        </Form.Item>
      </Form>
    </Drawer>
  );
};

export default Drawers;
