// React
import React, { Component, Fragment } from "react";

// Third part
import { Layout, Menu, Typography } from "antd";
import { HashRouter as Router, Route, Link } from "react-router-dom";

// Custom
import "../../assets/css/EmployeeForm.css";
import Order from "./components/Order";
import Reservation from "./components/Reservation";
import Information from "./components/Information";

const { Header, Content, Footer } = Layout;

const { Text } = Typography;

export default class EmployeeBoard extends Component {
  render() {
    return (
      <Fragment>
        <Router>
          <Layout>
            <Header
              style={{
                position: "fixed",
                zIndex: 1,
                width: "100%",
                background: "#000000",
                display: "flex"
              }}
            >
              <Menu
                theme="dark"
                mode="horizontal"
                defaultSelectedKeys={["/order"]}
                style={{ lineHeight: "64px" }}
              >
                <Menu.Item disabled>
                  <Text className="brand" type="warning">
                    PLOV
                  </Text>
                </Menu.Item>
                <Menu.Item key="/order">
                  Order
                  <Link to="/order" />
                </Menu.Item>
                <Menu.Item key="2">
                  Reservation
                  <Link to="/reservation" />
                </Menu.Item>
                <Menu.Item key="3">
                  Info
                  <Link to="/info" />
                </Menu.Item>
              </Menu>
            </Header>
            <Content style={{ padding: "0 50px", marginTop: 94 }}>
              <Route exact strict path="/order" component={Order} />
              <Route exact strict path="/reservation" component={Reservation} />
              <Route exact strict path="/info" component={Information} />
              <Route />
              <Route />
            </Content>
            <Footer style={{ textAlign: "center" }}>
              PLOV ©2020 Created by Arian
            </Footer>
          </Layout>
        </Router>
      </Fragment>
    );
  }
}
