// React
import React from "react";

// Third part
import { Layout, Menu } from "antd";
import { LogoutOutlined } from "@ant-design/icons";
import { Chart, Axis, Tooltip, Geom } from "bizcharts";
import { HashRouter as Router, Route, Link, Switch } from "react-router-dom";

// Custom
import { Breakfast, PersonalIcon, TableInfo } from "../../assets/";
import { Dish, Personal, Table } from "./components/";
import { Footer } from "../custom/";
const { Header, Content } = Layout;

const AdminBoard = ({ match, history }) => {
  return (
    <Router>
      <Layout style={{ minHeight: "100vh" }}>
        <Header
          className="header"
          style={{
            position: "fixed",
            zIndex: 1,
            width: "100%",
            background: "#ffffff"
          }}
        >
          <Menu
            mode="horizontal"
            style={{ height: "100%", textAlign: "center" }}
          >
            <Menu.Item disabled>
              <div className="text-uppercase font-weight-bold">
                {match.params.name}
              </div>
            </Menu.Item>
            <Menu.Item key="1">
              <Breakfast />
              <span className=" menu-title ml-2">Dish Information</span>
              <Link to="/dish" />
            </Menu.Item>

            <Menu.Item key="2">
              <PersonalIcon />
              <span className="menu-title ml-2">Personal</span>
              <Link to="/personal" />
            </Menu.Item>
            <Menu.Item key="3">
              <TableInfo />
              <span className=" menu-title ml-2">Table</span>
              <Link to="/table" />
            </Menu.Item>
            <Menu.Item key="4" onClick={() => history.replace("/")}>
              <LogoutOutlined style={{ fontSize: "20px" }} />
              <span className="menu-title ml-2">Logout</span>
            </Menu.Item>
          </Menu>
        </Header>

        <Content
          className="site-layout-background"
          style={{ padding: "0 50px", marginTop: 64 }}
        >
          <div
            className="site-layout-background"
            style={{ padding: 24, minHeight: 380 }}
          >
            <Chart height={400} data={data} forceFit>
              <Axis name="month" />
              <Axis
                name="temperature"
                label={{ formatter: val => `${val}°C` }}
              />
              <Tooltip crosshairs={{ type: "y" }} />
              <Geom
                type="line"
                position="month*temperature"
                size={2}
                color={"city"}
              />
              <Geom
                type="point"
                position="month*temperature"
                size={4}
                color={"city"}
              />
            </Chart>
            <Switch>
              <Route path="/dish" component={Dish} />
              <Route path="/personal" component={Personal} />
              <Route path="/table" component={Table} />
            </Switch>
          </div>
        </Content>
        <Footer />
      </Layout>
    </Router>
  );
};

export default AdminBoard;
