import React from "react";
import {
  Drawer,
  Row,
  Col,
  Select,
  Input,
  InputNumber,
  Divider,
  Button
} from "antd";
const OrderDrawer = ({
  visible,
  onClose,
  table_number,
  name,
  order,
  numberChange,
  nameChange,
  filteredOptions,
  orderSelect,
  handleOrder
}) => {
  return (
    <Drawer
      title="Add new Order"
      width={420}
      onClose={onClose}
      visible={visible}
      bodyStyle={{ paddingBottom: 80 }}
    >
      <Row gutter={[40, 20]}>
        <Col span={10}>
          <InputNumber
            placeholder="Enter table number"
            style={{ width: "150px" }}
            value={table_number}
            onChange={numberChange}
          />
        </Col>
        <Col span={10}>
          <Input
            style={{ width: "150px" }}
            placeholder="Enter customer"
            value={name}
            onChange={nameChange}
            allowClear
          />
        </Col>
      </Row>
      <Row gutter={[20, 20]}>
        <Col span={10}>
          <Select
            mode="multiple"
            style={{ width: "100%" }}
            placeholder="Order"
            value={order}
            onChange={orderSelect}
          >
            {filteredOptions.map(item => (
              <Select.Option key={item} value={item}>
                {item}
              </Select.Option>
            ))}
          </Select>
        </Col>
      </Row>
      <Row>
        <Divider />
        <Col span={6}>
          <Button onClick={onClose} style={{ width: "100px" }} type="danger">
            Cancel
          </Button>
        </Col>
        <Col offset={2} span={6}>
          <Button
            style={{ width: "100px" }}
            type="primary"
            onClick={handleOrder}
          >
            Order
          </Button>
        </Col>
      </Row>
    </Drawer>
  );
};

export default OrderDrawer;
