import React, { Component } from "react";
import { Button, Icon, Divider } from "antd";
import ReservationDrawer from "../../common/reservationDrawer";
import ReservationList from "../../common/reservationList";

export default class Reservation extends Component {
  state = {
    data: [
      {
        id: 1,
        name: "Alex",
        table_number: "1",
        img:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        seats: "4",
        data: "2020-01-01 08:47:47"
      },
      {
        id: 2,
        name: "Mike",
        table_number: "2",
        img:
          "https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png",
        seats: "6",
        data: "2020-01-01 05:47:47"
      }
    ],
    visible: false,
    usernameValue: "",
    tableNumber: "",
    seats: "",
    date: ""
  };

  showDrawable = () => {
    this.setState({ visible: true });
  };

  onClose = () => {
    this.setState({ visible: false });
  };

  onNameChange = event => {
    this.setState({ usernameValue: event.target.value });
  };
  onNumberChange = number => {
    this.setState({ tableNumber: number });
  };

  onHandleAdd = () => {
    console.log("added");
  };
  onDataChange = (value, dateString) => {
    console.log(dateString);
    this.setState({ date: dateString });
  };
  onSeatsChange = value => {
    this.setState({ seats: value });
  };
  render() {
    const {
      visible,
      usernameValue,
      tableNumber,
      date,
      seats,
      data
    } = this.state;
    return (
      <div style={{ background: "#fff", padding: 24, minHeight: 680 }}>
        <Button
          type="primary"
          style={{ width: "150px" }}
          onClick={this.showDrawable}
        >
          <Icon type="plus" />
          New Reserv
        </Button>
        <Divider />
        <ReservationList data={data} />
        <ReservationDrawer
          visible={visible}
          userName={usernameValue}
          tableNumber={tableNumber}
          seats={seats}
          onNameChange={this.onNameChange}
          onNumberChange={this.onNumberChange}
          onClose={this.onClose}
          onhandleAdd={this.onHandleAdd}
          onDataChange={this.onDataChange}
          onSeatsChange={this.onSeatsChange}
        />
      </div>
    );
  }
}
