// React
import React, { useEffect, useState, Fragment } from "react";

// Third part
import { Layout, Divider } from "antd";

import { Order, getOrder } from "../../../../../services/orderServices";

// Custom
import {
  HeaderTitle,
  CustomDrawerButton,
  openNotification
} from "../../../../custom/";
import * as All from "./";

const { Content } = Layout;
const OrderContent = () => {
  // state
  const [DrawerVisible, setDrawerVisible] = useState();
  const [orderData, setOrderData] = useState([]);

  const getData = async () => {
    try {
      await getOrder()
        .then(res => res.data)
        .then(data => setOrderData(data));
    } catch (err) {
      openNotification("error", "Can't get data", "Check connection");
    }
  };

  useState(() => {
    getData();
  }, []);

  console.log();
  // showDrawer
  const showDrawer = () => {
    setDrawerVisible(true);
  };
  const onClose = () => {
    setDrawerVisible(false);
  };
  const handleAdd = async items => {
    try {
      await Order(items);
    } catch (error) {
      openNotification("error", "Cant order", "Check Connection");
    }
  };

  return (
    <Fragment>
      <Content style={{ margin: "2em 16px" }}>
        <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
          <HeaderTitle title={"Order Menu"} />
          <CustomDrawerButton title={"Take order"} showDrawer={showDrawer} />
          <Divider />
          <All.OrderDrawer
            visible={DrawerVisible}
            onClose={onClose}
            handleAdd={handleAdd}
          />
        </div>
      </Content>
    </Fragment>
  );
};

export default OrderContent;
