//React
import React, { Fragment, useState, useEffect } from "react";

// Third part
import { Layout, InputNumber, Divider, Row, Col, Card } from "antd";

// Tools
import _ from "lodash";

// Services
import { getOrder } from "../../../../services/orderServices";

// custom Components
import { openNotification } from "../../../custom/";

const Search = () => {
  // state
  const [orderData, setOrderData] = useState([]);
  const [searchData, setSearchData] = useState({});
  // searchMethod
  const SearchOrders = value => {
    const table = _.find(orderData, ["table", value]);
    const newData = { ...table };
    setSearchData(newData);
  };
  // fetch dataMethod
  const getData = async () => {
    try {
      await getOrder()
        .then(res => res.data)
        .then(data => setOrderData(data));
    } catch (err) {
      openNotification("error", "Can't get data", "Check connection");
    }
  };
  // Hook
  useEffect(() => {
    getData();
  }, []);

  return (
    <Fragment>
      <Layout>
        <Layout.Content
          style={{ padding: "0 50", minHeight: 580, background: "#ffffff" }}
        >
          <Row>
            <Col span={12}>Title</Col>
            <Col span={12}>
              <InputNumber
                min={0}
                placeholder="Input TableNumber"
                className="w-50"
                onChange={value => SearchOrders(value)}
              />
              <div style={{ width: "50%" }}>
                <Divider dashed />
              </div>
              <Card
                className="text-center"
                style={{ width: "50%" }}
                title={searchData.table}
                cover={searchData.customer}
              ></Card>
            </Col>
          </Row>
        </Layout.Content>
      </Layout>
    </Fragment>
  );
};

export default Search;
